using System;
using System.Linq;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Identity;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Logging;

namespace AOR.Data;

public static class AorDbSeeder
{
    /// <summary>
    /// Seed roles and test users into the Identity store.
    /// Call this during application startup after DbContext has been registered.
    /// </summary>
    public static async Task SeedAsync(IServiceProvider serviceProvider, ILogger logger)
    {
        using var scope = serviceProvider.CreateScope();
        var scoped = scope.ServiceProvider;

        // Try to log DB provider if available
        try
        {
            var db = scoped.GetService<AorDbContext>();
            if (db != null)
            {
                logger.LogInformation("Database provider: {Provider}", db.Database.ProviderName ?? "(unknown)");
            }
        }
        catch { /* ignore logging errors */ }

        RoleManager<IdentityRole> roleManager;
        UserManager<User> userManager;
        try
        {
            roleManager = scoped.GetRequiredService<RoleManager<IdentityRole>>();
            userManager = scoped.GetRequiredService<UserManager<User>>();
        }
        catch (Exception ex)
        {
            logger.LogWarning(ex, "RoleManager or UserManager not available; skipping identity seeding.");
            return;
        }

        // Roles to ensure
        var roles = new[] { "Crew", "Admin", "Registrar" };
        foreach (var role in roles)
        {
            if (!await roleManager.RoleExistsAsync(role))
            {
                var createResult = await roleManager.CreateAsync(new IdentityRole(role));
                if (createResult.Succeeded)
                {
                    logger.LogInformation("Created role '{Role}'", role);
                }
                else
                {
                    logger.LogWarning("Failed to create role '{Role}': {Errors}", role, string.Join("; ", createResult.Errors.Select(e => e.Description)));
                }
            }
            else
            {
                logger.LogInformation("Role '{Role}' already exists", role);
            }
        }

        // Test users
        var testUsers = new[]
        {
            new { Email = "crew@test.local", Password = "Test123$", Role = "Crew", FirstName = "Crew", LastName = "User" },
            new { Email = "admin@test.local", Password = "Test123$", Role = "Admin", FirstName = "Admin", LastName = "User" },
            new { Email = "registrar@test.local", Password = "Test123$", Role = "Registrar", FirstName = "Registrar", LastName = "User" }
        };

        foreach (var tu in testUsers)
        {
            try
            {
                var existing = await userManager.FindByEmailAsync(tu.Email);
                if (existing == null)
                {
                    var user = new User
                    {
                        UserName = tu.Email,
                        Email = tu.Email,
                        EmailConfirmed = true,
                        FirstName = tu.FirstName,
                        LastName = tu.LastName
                    };

                    var createRes = await userManager.CreateAsync(user, tu.Password);
                    if (!createRes.Succeeded)
                    {
                        logger.LogWarning("Failed to create user {Email}: {Errors}", tu.Email, string.Join("; ", createRes.Errors.Select(e => e.Description)));
                        continue;
                    }

                    var addRoleRes = await userManager.AddToRoleAsync(user, tu.Role);
                    if (!addRoleRes.Succeeded)
                    {
                        logger.LogWarning("Failed to add role {Role} to user {Email}: {Errors}", tu.Role, tu.Email, string.Join("; ", addRoleRes.Errors.Select(e => e.Description)));
                    }
                    else
                    {
                        logger.LogInformation("Created user {Email} with role {Role}", tu.Email, tu.Role);
                    }
                }
                else
                {
                    // Ensure role is assigned
                    var rolesForUser = await userManager.GetRolesAsync(existing);
                    if (!rolesForUser.Contains(tu.Role))
                    {
                        var addRoleRes = await userManager.AddToRoleAsync(existing, tu.Role);
                        if (!addRoleRes.Succeeded)
                        {
                            logger.LogWarning("Failed to add missing role {Role} to existing user {Email}: {Errors}", tu.Role, tu.Email, string.Join("; ", addRoleRes.Errors.Select(e => e.Description)));
                        }
                        else
                        {
                            logger.LogInformation("Added missing role {Role} to existing user {Email}", tu.Role, tu.Email);
                        }
                    }
                    else
                    {
                        logger.LogInformation("User {Email} already exists and has role {Role}", tu.Email, tu.Role);
                    }
                }
            }
            catch (Exception ex)
            {
                logger.LogError(ex, "Exception while seeding user {Email}", tu.Email);
            }
        }
    }
}

using Microsoft.EntityFrameworkCore;
using AOR.Data;
using Microsoft.AspNetCore.Authentication.Cookies;
using Microsoft.AspNetCore.Identity;


var builder = WebApplication.CreateBuilder(args);

// Add services
builder.Services.AddControllersWithViews();

// Database configuration - MySQL
var connectionString = builder.Configuration.GetConnectionString("AorDb");
builder.Services.AddDbContext<AorDbContext>(options =>
    options.UseMySql(connectionString, new MySqlServerVersion(new Version(11, 4, 0))));

// Identity - registered after DbContext so stores are available
// Use full AddIdentity so SignInManager, UserManager, RoleManager and cookie handling are configured
builder.Services.AddIdentity<User, IdentityRole>(options =>
{
    // Optional: tweak password requirements for dev/test convenience
    options.Password.RequireDigit = true;
    options.Password.RequireLowercase = true;
    options.Password.RequireUppercase = false;
    options.Password.RequireNonAlphanumeric = false;
    options.Password.RequiredLength = 6;
})
    .AddEntityFrameworkStores<AorDbContext>()
    .AddDefaultTokenProviders();

// Configure application cookie (custom login path)
builder.Services.ConfigureApplicationCookie(options =>
{
    options.LoginPath = "/LogIn";
    options.AccessDeniedPath = "/LogIn/AccessDenied";
});

var app = builder.Build();

using (var scope = app.Services.CreateScope())
{
    var sp = scope.ServiceProvider;
    var db = sp.GetRequiredService<AOR.Data.AorDbContext>();

    await db.Database.MigrateAsync();                 // <- migrate

    // Hent logger fra DI og pass både service provider og logger til seederen
    var logger = sp.GetRequiredService<ILogger<Program>>();
    await AOR.Data.AorDbSeeder.SeedAsync(sp, logger);  // <- SEED (med riktige argumenter)
}


// Configure pipeline
if (app.Environment.IsDevelopment())
{
    app.UseDeveloperExceptionPage();
}
else
{
    app.UseExceptionHandler("/Home/Error");
    app.UseHsts();
}

app.UseHttpsRedirection();
app.UseStaticFiles();
app.UseRouting();

// Disable caching so user cannot go back after logout
app.Use(async (context, next) =>
{
    context.Response.Headers["Cache-Control"] = "no-cache, no-store, must-revalidate";
    context.Response.Headers["Pragma"] = "no-cache";
    context.Response.Headers["Expires"] = "0";
    await next();
});

app.UseAuthentication();
app.UseAuthorization();

app.MapControllerRoute(
    name: "default",
    pattern: "{controller=LogIn}/{action=Index}/{id?}");

app.Run();

using Microsoft.EntityFrameworkCore;
using AOR.Data;
using Microsoft.AspNetCore.Authentication.Cookies;
using Microsoft.AspNetCore.Identity;


var builder = WebApplication.CreateBuilder(args);

// Add services
builder.Services.AddControllersWithViews();

// Database configuration - MySQL
var connectionString = builder.Configuration.GetConnectionString("AorDb");
builder.Services.AddDbContext<AorDbContext>(options =>
    options.UseMySql(connectionString, new MySqlServerVersion(new Version(11, 4, 0))));

// Identity - registered after DbContext so stores are available
// Use full AddIdentity so SignInManager, UserManager, RoleManager and cookie handling are configured
builder.Services.AddIdentity<User, IdentityRole>(options =>
{
    // Optional: tweak password requirements for dev/test convenience
    options.Password.RequireDigit = true;
    options.Password.RequireLowercase = true;
    options.Password.RequireUppercase = false;
    options.Password.RequireNonAlphanumeric = false;
    options.Password.RequiredLength = 6;
})
    .AddEntityFrameworkStores<AorDbContext>()
    .AddDefaultTokenProviders();

// Configure application cookie (custom login path)
builder.Services.ConfigureApplicationCookie(options =>
{
    options.LoginPath = "/LogIn";
    options.AccessDeniedPath = "/LogIn/AccessDenied";
});

var app = builder.Build();

using (var scope = app.Services.CreateScope())
{
    var sp = scope.ServiceProvider;

    try
    {
        var db = sp.GetRequiredService<AOR.Data.AorDbContext>();

        // Migrate + seed inside try so app doesn't crash when DB isn't reachable
        await db.Database.MigrateAsync();                 // <- migrate

        // Hent logger fra DI og pass både service provider og logger til seederen
        var logger = sp.GetRequiredService<ILogger<Program>>();
        await AOR.Data.AorDbSeeder.SeedAsync(sp, logger);  // <- SEED (med riktige argumenter)
    }
    catch (Exception ex)
    {
        // If DB isn't reachable or migration fails, log and continue so web app can start
        var logger = sp.GetService<ILogger<Program>>();
        if (logger != null)
        {
            logger.LogError(ex, "Database migrate/seed failed at startup. Application will continue to run.");
        }
        else
        {
            Console.Error.WriteLine("Database migrate/seed failed: " + ex);
        }
    }
}


// Configure pipeline
if (app.Environment.IsDevelopment())
{
    app.UseDeveloperExceptionPage();
}
else
{
    app.UseExceptionHandler("/Home/Error");
    app.UseHsts();
}

app.UseHttpsRedirection();
app.UseStaticFiles();
app.UseRouting();

// Disable caching so user cannot go back after logout
app.Use(async (context, next) =>
{
    context.Response.Headers["Cache-Control"] = "no-cache, no-store, must-revalidate";
    context.Response.Headers["Pragma"] = "no-cache";
    context.Response.Headers["Expires"] = "0";
    await next();
});

app.UseAuthentication();
app.UseAuthorization();

app.MapControllerRoute(
    name: "default",
    pattern: "{controller=LogIn}/{action=Index}/{id?}");

app.Run();
